const GET_RECIPES = "GET_RECIPES";
const FILTER_BY_DIET = "FILTER_BY_DIET";
const ORDER_BY_TITLE = "ORDER_BY_TITLE";
const ORDER_BY_SCORE = "ORDER_BY_SCORE";
const GET_BY_TITLE = "GET_BY_TITLE";
const GET_BY_ID = "GET_BY_ID";
const GET_BY_DIET = "GET_BY_ID";

export const initialState = {
  recipes: [],
  allRecipes: [],
  details: [],
  typeDiet: [],
};

function rootReducer(state = initialState, action) {
  switch (action.type) {
    //---------------------
    case GET_RECIPES:
      return {
        ...state,
        recipes: action.payload,
        allRecipes: action.payload,
      };
      //---------------------
    case GET_BY_TITLE:
      return ({ 
        ...state, 
        recipes: action.payload,
      }, console.log("deberia funcionar title: ", action.payload))
      //---------------------
    case FILTER_BY_DIET:
      const allRecipes = state.allRecipes
      const typeDietFilter = action.payload==="All"
      ?allRecipes
      :allRecipes.filter(el=>el.typeDiet.find(el=>el.title===action.payload))
      return {
        ...state,
        recipes:typeDietFilter
      }

    default:
      return state;
  }
}

export default rootReducer;
