import React, { useState } from "react";
import SearchBar from "../components/SearchBar";
import ContainerCards from "../components/ContainerCards";
import NavBar from "../components/NavBar";
import { useSelector } from "react-redux";
import Paginado from "../components/Paginado";

export default function Home() {
  const allRecipes = useSelector((state) => state.recipes);
  //currentPage = pagina actual
  const [currentPage, setCurrentPage] = useState(1);
  //itemsPerPage = catidad de items por pagina
  const [itemsPerPage, setItemsPerPage] = useState(9);
  //
  const indexOfLastItem = currentPage * itemsPerPage; //
  const indexOfFirstItem = indexOfLastItem - itemsPerPage; //
  const currentItems = allRecipes.slice( indexOfFirstItem,indexOfLastItem);
//
  const paginado=(pageNumber)=>{
    setCurrentPage(pageNumber)
  }

  return (
    <div>
      <NavBar />
      <SearchBar />
      <Paginado itemsPerPage={itemsPerPage} allItems={allRecipes} paginado={paginado}/>
      <ContainerCards currentItems={currentItems}/>
    </div>
  );
}
