import React,{useState} from "react";
import { useDispatch } from "react-redux";
import { filterDiet,filterCreated,orderTitle } from "../redux/actions";

export default function NavBar({setCurrentPage}) {
  const dispatch = useDispatch();
  const [orden,setOrden]=useState("");

  function handleDiet(e) {
    dispatch(filterDiet(e.target.value));
  }
  function handleFilterCreated(e){
    dispatch(filterCreated(e.target.value));
  }
  function handleSort(e){
   // console.log("deberia funcionar")
    e.preventDefault()
    dispatch(orderTitle(e.target.value))
    setOrden(`Ordenado ${e.target.value}`)
    //console.log("deberia funcionar")
  }

  return (
    <div>
      <button>Volver a Recargar Recetas</button>
      <div>
        <select onChange={(el)=>handleSort(el)}>
          <option value="asc">Ascendent(A-Z)</option>
          <option value="des">Descendent(Z-A )</option>
        </select>
        <select>
          <option>mayor a menor puntuacion</option>
          <option>menor a mayor puntuacion</option>
        </select>
        <select onChange={(el) => handleDiet(el)}>
          <option value="All">All recipes</option>
          <option value="gluten free">Gluten Free</option>
          <option value="ketogenic">Ketogenic</option>
          <option value="lacto ovo vegetarian">Ovo-Vegetarian</option>
          <option value="vegan">Vegan</option>
          <option value="pescatarian">Pescatarian</option>
          <option value="paleolithic">Paleolithic</option>
          <option value="primal">Primal</option>
          <option value="whole 30">Whole 30</option>
        </select>
        <select onChange={(el)=>handleFilterCreated(el)}>
          <option value="all">created-existing</option>
          <option value="created">created</option>
          <option value="existing">existing</option>
        </select>
      </div>
    </div>
  );
}
