import React from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getRecipesId } from "../redux/actions.js";
import { useEffect } from "react";
import { useParams } from "react-router-dom";
import Loading from "./Loading"

import { resetDetail } from "../redux/actions.js";

import s from "../styles/Details.module.css"



export default function Details() {

 


  const dispatch = useDispatch();
  const {id} = useParams();
  const detailsState = useSelector((state) => state.details ) 

  function handleReset(e){
  e.prevenDefault()
  dispatch(resetDetail)
  }

  useEffect(() => {
    dispatch(getRecipesId(id));
  }, []);

  const myRecipe = useSelector((state)=>state.details)
  return (  
    <div>
        
        <Link to="/home"><button className={s.button}>Home</button></Link>

        <div className={s.container}>
        {myRecipe.length>0?
            <div>
                <h1>{myRecipe[0].title}</h1>
                <img className={s.img} src={myRecipe[0].img?myRecipe[0].img:"https://st.depositphotos.com/1036708/2191/i/600/depositphotos_21918797-stock-photo-knife-and-fork-with-plate.jpg"}/>
                <h2>Type Diet</h2>
                <p> {myRecipe[0].TypeDiet.map(el=>el.name)}</p>
                <div>
                <h2>Dish Types</h2>
                <p> {myRecipe[0].dishTypes[0].name}</p>
                </div>
                <h2>Summary</h2>
                <p> {myRecipe[0].summary}</p>
                <h2>HealthScore</h2>
                <h5>{myRecipe[0].healthScore}/100</h5>
                <h5>Analyzed Instructions: {Array.isArray(myRecipe[0].analyzedInstructions)?myRecipe[0].analyzedInstructions.map(el=>el.steps.map(elem=>elem.step)):myRecipe[0].analyzedInstructions}</h5>
            </div>:
        <Loading/>}
        {console.log("hola el componente Detail: ",myRecipe)}
        </div>
    </div>
  )
}
