import React, { useState, useEffect } from "react";
import { Link, useHistory } from "react-router-dom";
import { postRecipes, getTypeDiet } from "../redux/actions";
import { useDispatch, useSelector } from "react-redux";


function controlForm(input){
  const lim = new RegExp("^[0-9]+$")
  let errors = {}
  if(!input.title) errors.title = "you should enter a title"
  if(!input.summary) errors.summary="you should enter a summary"
  if(input.healthScore < 0 || input.healthScore > 100 || !reg.test(input.healthScore))errors
}
//----------------------------
export function Create() {
  //----------- CONSTANTES LOCALES Y GLOBALES------------------------
  const dispatch = useDispatch();
  const typeDiet = useSelector((state) => state.typeDiet);
//------------- FUNCIONES ------------------------
const [input,setInput]= useState({
    title:"",
    summary:"",
    healthScore:"",
    analyzedInstructions:"",
    typeDiet:[]

 })


useEffect(()=>{
  dispatch(getTypeDiet())
},[])

//-------------------- RENDERIZADO -----------------------------------------------------------------------------------
return (
  <div>
    <Link to="/home">
      <button>BACK</button>
    </Link>
    <h1>Crea tu receta</h1>
    <form>
      <div>
        <label>title:</label>
        <input type="text" value={input.name} name="name"/>
      </div>
      <div>
        <label>summary:</label>
        <input type="text" name="summary" value={input.summary}/>
      </div>
      <div>
        <label>healthScore:</label>
        <input type="text" name="healthScore" value={input.healthScore}/>
      </div>
      <div>
        <label>analyzedInstructions</label>
        <input type="text" name="analyzedInstructions" value={input.analyzedInstructions}/>
      </div>
      <select></select>
    </form>
  </div>
  )
}
