import { useEffect,useState } from "react";
import { useDispatch, useSelector } from "react-redux";
//importo desde las actions de redux
import {getTitle} from "../redux/actions.js"



export default function SearchBar(){
    //----------------------------------
    const dispatch=useDispatch();
    const[title,setTitle]=useState("");
    //----------------------------------
    function handleInputChange(e){
        e.preventDefault();
        setTitle(e.target.value);
    }
    //----------------------------------
    function handleSubmit(e){
        e.preventDefault();
        if(title!==""){
            //dispacho la accion que cree en actions
            dispatch(getTitle(title));
            console.log("")
            //setTitle("",getTitle(title))
        }else{
            alert("Debe ingresar el nombre de una receta.")
        }
    }
    //----------------------------------
    return (<div>
        <form onSubmit={handleSubmit}>
            <label htmlFor="nombre">Search Diet: </label>
            <input type="text" value={title} onChange={handleInputChange} placeholder="Insert Diet Name"></input>
            <button type="submit" onClick={handleSubmit}>SEARCH</button>
        </form>
    </div>)
}
