import React from "react";
import { Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import { getRecipesId } from "../redux/actions.js";
import { useEffect } from "react";
import Loading from "./Loading"

export default function Details(props) {
  const dispatch = useDispatch();

  useEffect(() => {
    dispatch(getRecipesId(props.match.params.id));
  }, [dispatch]);

  const myRecipe= useSelector((state)=>state.details)
  return (
    <div>
        <Link to="/home"><button>Home</button></Link>
        {myRecipe.length>0?<div>
            <h1>{myRecipe[0].title}</h1>
            <img src={myRecipe[0].img?myRecipe[0]:<h5>no encontre img</h5>}/>
            <h3>Type Diet: {myRecipe[0].typeDiets.map(el=>el.name)}</h3>
            <h4>Summary: {myRecipe[0].summary}</h4>
            <h5>HealtScore: {myRecipe[0].healtScore}</h5>
            <h5>step: {Array.isArray(myRecipe[0])}</h5>
        </div>:<Loading/>}
        
    </div>
  )
}
